        severity = args.get('severity', 'high')
        sns.publish(
            TopicArn=ALERT_TOPIC_ARN,
            Message=msg,
            Subject=f"üö® DevOps Alert ({severity.upper()})"
        )
        return "Human notified"

    else:
        return f"Unknown tool: {name}"

def lambda_handler(event, context):
    print("üö® Incident detected:", json.dumps(event))

    # 1. Get log context
    log_summary = run_logs_insights_query(LOG_GROUP)
    incident_input = f"CloudWatch alarm triggered. Recent errors:\n{log_summary}"

    # 2. Invoke Bedrock Agent
    try:
        response = bedrock_agent.invoke_agent(
            agentId=AGENT_ID,
            agentAliasId=AGENT_ALIAS_ID,
            sessionId=context.aws_request_id,
            inputText=incident_input
        )

        # 3. Parse agent's tool decision
        tool_use = parse_agent_response(response['completion'])
        if tool_use:
            result = execute_tool(tool_use)
            print("‚úÖ Action taken:", result)
        else:
            # Fallback: notify human if no tool selected
            sns.publish(
                TopicArn=ALERT_TOPIC_ARN,
                Message=f"Agent did not select a tool. Input: {incident_input}",
                Subject="‚ö†Ô∏è Agent Ambiguity"
            )
            print("‚ö†Ô∏è No tool selected ‚Äì human notified")

    except Exception as e:
        print("‚ùå Agent invocation failed:", e)
        sns.publish(
            TopicArn=ALERT_TOPIC_ARN,
            Message=f"Agent runtime error: {str(e)}",
            Subject="üí• DevOps Agent Crash"
        )

    return {"status": "completed"}
